class PomodokeTimer {
    constructor() {
        this.timeLeft = 20 * 60; // 20 minutes in seconds
        this.timerId = null;
        this.isRunning = false;
        
        // Timer durations in minutes
        this.durations = {
            pomodoke: 25,
            shortBreak: 5,
            longBreak: 15
        };

        this.initializeElements();
        this.initializeEventListeners();
        this.updateDisplay();
        this.initializeThemeSwitch();
        
        // Initially hide pause button
        this.pauseButton.classList.add('hidden');
    }

    initializeElements() {
        this.minutesDisplay = document.getElementById('minutes');
        this.secondsDisplay = document.getElementById('seconds');
        this.startButton = document.getElementById('start');
        this.pauseButton = document.getElementById('pause');
        this.pomodokeButton = document.getElementById('pomodoke');
        this.shortBreakButton = document.getElementById('short-break');
        this.longBreakButton = document.getElementById('long-break');
    }

    initializeEventListeners() {
        this.startButton.addEventListener('click', () => this.start());
        this.pauseButton.addEventListener('click', () => this.pause());
        this.pomodokeButton.addEventListener('click', () => this.setTimer('pomodoke'));
        this.shortBreakButton.addEventListener('click', () => this.setTimer('shortBreak'));
        this.longBreakButton.addEventListener('click', () => this.setTimer('longBreak'));
    }

    updateDisplay() {
        const minutes = Math.floor(this.timeLeft / 60);
        const seconds = this.timeLeft % 60;
        const timeString = `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
        
        // Update display elements
        this.minutesDisplay.textContent = minutes.toString().padStart(2, '0');
        this.secondsDisplay.textContent = seconds.toString().padStart(2, '0');
        
        // Update browser tab title
        document.title = `${timeString} - Pomodoke Timer`;
    }

    start() {
        if (!this.isRunning) {
            this.isRunning = true;
            this.timerId = setInterval(() => {
                this.timeLeft--;
                this.updateDisplay();
                
                if (this.timeLeft === 0) {
                    this.playAlarm();
                    this.reset();
                }
            }, 1000);

            // Show pause button and make it primary
            this.pauseButton.classList.remove('hidden');
            this.pauseButton.classList.add('primary');
            // Change start button text to "Reset"
            this.startButton.classList.remove('primary');
            this.startButton.textContent = 'Reset';
        } else {
            // If timer is running, act as reset
            this.reset();
        }
    }

    pause() {
        this.isRunning = false;
        clearInterval(this.timerId);
        
        // Update button states
        this.pauseButton.classList.remove('primary');
        this.pauseButton.classList.add('hidden');
        this.startButton.classList.add('primary');
        this.startButton.textContent = 'Resume';
    }

    reset() {
        this.isRunning = false;
        clearInterval(this.timerId);
        this.timeLeft = this.durations[this.currentMode] * 60;
        this.updateDisplay();
        
        // Reset button states
        this.pauseButton.classList.remove('primary');
        this.pauseButton.classList.add('hidden');
        this.startButton.classList.add('primary');
        this.startButton.textContent = 'Start';
    }

    setTimer(mode) {
        this.pause();
        this.currentMode = mode;
        this.timeLeft = this.durations[mode] * 60;
        this.updateDisplay();
        
        // Update active button
        [this.pomodokeButton, this.shortBreakButton, this.longBreakButton].forEach(button => {
            button.classList.remove('active');
        });
        
        // Reset button states
        this.pauseButton.classList.remove('primary');
        this.pauseButton.classList.add('hidden');
        this.startButton.classList.add('primary');
        this.startButton.textContent = 'Start';
        
        switch(mode) {
            case 'pomodoke':
                this.pomodokeButton.classList.add('active');
                break;
            case 'shortBreak':
                this.shortBreakButton.classList.add('active');
                break;
            case 'longBreak':
                this.longBreakButton.classList.add('active');
                break;
        }
    }

    playAlarm() {
        const audio = new Audio('data:audio/wav;base64,');
        
        // Different number of beeps based on mode
        if (this.currentMode === 'pomodoke') {
            // 5 beeps for pomodoro
            audio.play();
            setTimeout(() => audio.play(), 500);
            setTimeout(() => audio.play(), 1000);
            setTimeout(() => audio.play(), 1500);
            setTimeout(() => audio.play(), 2000);
        } else {
            // 3 beeps for breaks
            audio.play();
            setTimeout(() => audio.play(), 500);
            setTimeout(() => audio.play(), 1000);
        }
    }

    initializeThemeSwitch() {
        const themeToggle = document.getElementById('theme-toggle');
        themeToggle.addEventListener('click', () => {
            const root = document.documentElement;
            const isAmber = root.getAttribute('data-theme') === 'amber';
            
            if (isAmber) {
                root.removeAttribute('data-theme');
                themeToggle.textContent = 'GREEN';
            } else {
                root.setAttribute('data-theme', 'amber');
                themeToggle.textContent = 'AMBER';
            }
        });
    }
}

// Initialize the timer when the page loads
let timer;
document.addEventListener('DOMContentLoaded', () => {
    timer = new PomodokeTimer();
});
